<!-- wp:designsetgo/accordion {"className":"dsgo-accordion--icon-chevron","style":{"spacing":{"margin":{"top":"var:preset|spacing|40"}}}} -->
<div class="wp-block-designsetgo-accordion dsgo-accordion dsgo-accordion--icon-right dsgo-accordion--border-between dsgo-accordion--icon-chevron" style="margin-top:var(--wp--preset--spacing--40);--dsgo-accordion-border-color:var(--wp--preset--color--contrast);--dsgo-accordion-item-gap:0.5rem" data-allow-multiple="false" data-icon-style="chevron"><div class="dsgo-accordion__items"><!-- wp:designsetgo/accordion-item {"title":"How long does shipping take?","isOpen":true} -->
<div class="dsgo-accordion-item dsgo-accordion-item--open" data-initially-open="true"><button class="dsgo-accordion-item__header" aria-expanded="true"><span class="dsgo-accordion-item__title">How long does shipping take?</span><span class="dsgo-accordion-item__icon"></span></button><div class="dsgo-accordion-item__content" style="display:block"><!-- wp:paragraph -->
